import React from "react";
import Calendar from "./Calendar.js";
import format from "date-fns/format";
import { useState, useEffect } from "react";

const CalendarOutput = (props) => {
  const [freeTimes, setFreeTimes] = useState();
  // const [outputReady, setOutputReady] = useState(false)

  const fetchFreeTimes2 = () => {
    const busyTimes = [
      ["2022-08-24T14:00:00.000Z", "2022-08-24T19:00:00.000Z"],
      ["2022-08-24T19:15:00.000Z", "2022-08-24T20:30:00.000Z"],
      ["2022-08-24T21:30:00.000Z", "2022-08-24T22:30:00.000Z"],
      ["2022-08-25T15:00:00.000Z", "2022-08-25T16:00:00.000Z"],
      ["2022-08-25T16:30:00.000Z", "2022-08-25T17:30:00.000Z"],
      ["2022-08-25T18:30:00.000Z", "2022-08-25T19:30:00.000Z"],
      ["2022-08-26T15:00:00.000Z", "2022-08-26T18:45:00.000Z"],
    ];

    const startDate = "2022-08-23T12:00:00.000Z";
    const endDate = "2022-08-27T03:59:00.000Z";
    var daysOfYear = [];
    for (
      var d = new Date(startDate);
      d <= new Date(endDate);
      d.setDate(d.getDate() + 1)
    ) {
      daysOfYear.push(new Date(d));
    }

    const finalPenguin = [];
    for (let i = 0; i < daysOfYear.length; i++) {
      var thisDay = new Date(daysOfYear[i]);
      var thisDayMorning = new Date(thisDay.setHours(6, 0, 0));

      for (let x = 0; x < busyTimes.length; x++) {
        var start = new Date(busyTimes[x][0]);
        var end = new Date(busyTimes[x][1]);
        if (sameDay(thisDayMorning, start)) {
          if (x === 0){
            var out = []
            var k = prettyTime(thisDayMorning)
            var n = prettyTime(start)

          }
        }
      }
    }

    console.log(daysOfYear);
    console.log(busyTimes);
  };

  function prettyTime(d1) { 
    var p = String(d1.toLocaleString('en-US', {timeZone: 'America/New_York'}))
    var splitted = p.split(" ")
    var day = splitted[0]
    var time = splitted[1] + " " + splitted[2]

    var output = [day, time]

    return( output ) }

  function sameDay(d1, d2) {
    return (
      d1.getFullYear() === d2.getFullYear() &&
      d1.getMonth() === d2.getMonth() &&
      d1.getDate() === d2.getDate()
    );
  }

  // end test

  const allStartTimes = [];
  const allEndTimes = [];
  const busyBlocksToAdd = [];

  var globalStart = "";
  var globalEnd = "";
  const fetchFreeTimes = () => {
    // Check if endDate is set, if not set to startDate

    // Format start date to 8am
    var start = new Date(props.startDate);
    start.setHours(8);
    start.setMinutes(0);
    start.setSeconds(0);
    start = start.toISOString();
    globalStart = start;

    // Format end date to 8 pm
    let end = "";
    if (props.endDate === null) {
      end = new Date(props.startDate);
      const piza = new Date(props.startDate);
      props.setEndDate(piza);
      end = new Date(piza);
    } else {
      end = new Date(props.endDate);
    }
    end.setHours(23);
    end.setMinutes(59);
    end.setSeconds(0);
    end = end.toISOString();
    globalEnd = end;

    console.log(start);
    console.log(end);

    // Build request body
    const request = {};
    request.timeMin = start;
    request.timeMax = end;
    request.timeZone = "America/New_York";

    // Create calendars object
    let cals = [];
    for (let i = 0; i < props.selectedCalendars.length; i++) {
      let calObj = {};
      calObj.id = props.selectedCalendars[i];
      cals.push(calObj);
    }

    request.items = cals;

    let num_days = "";
    if (props.endDate === null) {
      console.log("we here");
      let num_days = props.startDate.getDate() - props.startDate.getDate();
    } else {
      console.log("naw we here");
      num_days = props.endDate.getDate() - props.startDate.getDate();
      console.log(num_days);
    }
    console.log(num_days);
    num_days = num_days + 1;

    console.log("TUCKEr");
    console.log(props.endDate);
    return gapi.client.calendar.freebusy
      .query(request)

      .then(
        function (response) {
          let allTimes = [];
          for (let k = 0; k < props.selectedCalendars.length; k++) {
            var curCal = props.selectedCalendars[k];
            var busyTimes = response.result.calendars[curCal].busy;

            for (let i = 0; i < busyTimes.length; i++) {
              var busyBlock = busyTimes[i];
              console.log("Busy block for " + curCal, busyBlock);

              var startTime = busyBlock.start;
              var endTime = busyBlock.end;

              let test = [new Date(startTime), new Date(endTime)];
              allTimes.push(test);
            }
          }

          console.log("all times");
          console.log(allTimes);
          const merged = newfunc(allTimes);
          console.log("merged is");
          console.log(merged);
          finishUp2(merged);
        },
        function (err) {
          console.error("Execute error", err);
        }
      );
  };

  const newfunc = (times) => {
    console.log(times);
    console.log("woohhoo");
    times.sort(function (a, b) {
      return a[0] - b[0];
    });

    const merged = [];
    for (let i = 0; i < times.length; i++) {
      if (!merged.length) {
        merged.push(times[i]);
      } else if (merged[merged.length - 1][1] < times[i][0]) {
        merged.push(times[i]);
      } else {
        var left = merged[merged.length - 1][1];
        var right = times[i][1];

        var comp = Math.max(left, right);
        console.log(comp);
        comp = new Date(comp);
        merged[merged.length - 1][1] = comp;
      }
    }

    for (let i = 0; i < merged.length; i++) {
      const temp = new Date(merged[i][0]).toLocaleString();
      const temp2 = new Date(merged[i][1]).toLocaleString();

      console.log(temp);
      console.log(temp2);
      console.log("break");
    }

    return merged;
  };

  const unique_days2 = [];
  const finishUp2 = (finalArr) => {
    // const unique_days = []
    const temp = finalArr.map((obj, i, arr) => {
      const xx = getDate(obj[0]);
      if (unique_days2.includes(String(xx)) === false) {
        unique_days2.push(String(xx));
      } else {
        return "ignore";
      }
    });

    const motherToEndAll = [];
    for (let i = 0; i < unique_days2.length; i++) {
      const thisDay = unique_days2[i];
      const dkl = new Date(thisDay);
      const hihi = dkl.toLocaleDateString();

      const thisDayTimes = [];
      for (let j = 0; j < finalArr.length; j++) {
        const dayObj = finalArr[j];
        const firstTime = new Date(dayObj[0]);
        const secondTime = dayObj[1];
        const startDay2 = removeTime(firstTime);

        const freeTimeSlot = {};

        if (String(startDay2) === String(thisDay)) {
          const freeTimeSlot = [];
          const temp1 = String(dayObj[0].toLocaleString());
          const splitted1 = temp1.split(" ");

          freeTimeSlot.start = String(splitted1[1] + " " + splitted1[2]);

          const temp = String(dayObj[1].toLocaleString());
          const splitted = temp.split(" ");

          freeTimeSlot.end = String(splitted[1] + " " + splitted[2]);
          thisDayTimes.push(freeTimeSlot);
        }
      }

      const finalDolphin = [hihi, thisDayTimes];
      motherToEndAll.push(finalDolphin);
    }

    console.log("hard yeet");
    console.log(motherToEndAll);
    setFreeTimes(motherToEndAll);
    props.setOutputReady(true);
  };

  function removeTime(date = new Date()) {
    return new Date(date.toDateString());
  }

  const getDate = (inputDate) => {
    const d = new Date(inputDate);
    const dods = removeTime(d);
    return dods;
  };

  return (
    <div className="todo-container">
      {props.outputReady ? (
        <div>
          {freeTimes.map((test) => {
            console.log("yoyoyoyo");
            const thisDate = test[0];
            const thisDateTimeSlots = test[1];
            return (
              <div>
                <div>
                  <h2>{thisDate}:</h2>
                  <ul>
                    {thisDateTimeSlots.map((item) => {
                      console.log("item here");
                      console.log(item);
                      return (
                        <div>
                          <li>
                            {item.start} to {item.end}
                          </li>
                          {/* <p>Start time: {item.start}</p>
                            <p>End time: {item.end}</p> */}
                        </div>
                      );
                    })}
                  </ul>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <button className="btn-upload" onClick={fetchFreeTimes}>
          {" "}
          Fetch free times{" "}
        </button>
      )}
      <br />
      <br />
    </div>
  );
};

export default CalendarOutput;
