import React from "react"
import axios from "axios";
import { useState, useEffect} from 'react'
import GitHubIcon from '@material-ui/icons/GitHub'
import LinkedInIcon from '@material-ui/icons/LinkedIn'
import { about } from '../../portfolio'
import { ftf } from '../../portfolio'
import './FreeTimeFinder.css'


import { GoogleLogin, GoogleLogout } from 'react-google-login';
import { gapi } from 'gapi-script';

const FreeTimeFinder = () => {
  const { title, description, subhead, socidal } = ftf

  const [ profile, setProfile ] = useState(null);
  const clientId = '501334517326-6umils6sm37ai65fgh1v2r83s9ggefkg.apps.googleusercontent.com';

  const API_KEY = 'AIzaSyARUa77-dQ96I7e2tRCHCQexJcmUOEW9ZI';
  const DISCOVERY_DOCS = ["https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"];
  const SCOPES = "https://www.googleapis.com/auth/calendar.readonly";

  useEffect(() => {
    const initClient = () => {
        gapi.client.init({
            clientId: clientId,
            apiKey: API_KEY,
            discoveryDocs: DISCOVERY_DOCS,
            scope: SCOPES
            // scope: ''
        });


    };
    gapi.load('client:auth2', initClient);
  });

  const onSuccess = (res) => {
      setProfile(res.profileObj);
      // getCalendarIDs()
  };

  const onFailure = (err) => {
      console.log('failed', err);
  };

  const logOut = () => {
      setProfile(null);
  };


  return (

    <div className='about center'>

      <h1> {title} </h1>
      <h2 className='about__role'>{subhead} </h2>
      <p className='about__desc'>{description && description}</p>

      <br />
      <br />
      <br />
      {profile ? (
          <div>
              <img src={profile.imageUrl} alt="user image" />
              <h3>User Logged in</h3>
              <p>Name: {profile.name}</p>
              <p>Email Address: {profile.email}</p>
              <br />
              <br />

              <div id="checkboxes"> Your calendars: </div>




              <br />
              <GoogleLogout clientId={clientId} buttonText="Log out" onLogoutSuccess={logOut} />
          </div>
      ) : (
          <GoogleLogin
              clientId={clientId}
              buttonText="Sign in with Google"
              onSuccess={onSuccess}
              onFailure={onFailure}
              cookiePolicy={'single_host_origin'}
              isSignedIn={true}
          />
      )}


      {/* <button className="download-image-button" id="authorize_button" >Login via Google</button>
      <br />
    <button className="download-image-button" id="signout_button" >Sign Out</button> */}

    {/* <div id="checkboxes"> Your calendars: </div>

    <div id="freetimes"> Calendar free times: </div>
    <pre id="content" ></pre>

    <ul id="myList">
      <li>Select date(s) and person</li>
    </ul> */}

    </div>
  )
}

export default FreeTimeFinder


// Fetches calendar IDs, creates checkboxes and adds event listener
// called on site load
function getCalendarIDs() {
  return gapi.client.calendar.calendarList.list({})
      .then(function(response) {
        localStorage.clear();

        // Handle the results here (response.result has the parsed body).
        console.log("Response", response);
        console.log("YEEEET", response.result.items[0].id);

        var calendars = response.result.items;

        if (calendars.length > 0) {
        for (i = 0; i < calendars.length; i++) {
            var cal = calendars[i];
            var calName = cal.id;

            console.log("Calendar: ", calName)

            checkboxid = "checkbox";
            id_from_input = checkboxid.concat(i);

            var checkbox = document.createElement("input");
            var checkboxlabel = document.createElement("Label");

            checkbox.type = "checkbox";
            checkbox.class = "checkbox";
            checkbox.id = id_from_input;
            checkbox.value = calName;
            checkboxlabel.setAttribute("for",id_from_input);
            checkboxlabel.innerHTML = calName

            document.getElementById("checkboxes").appendChild(checkbox);
            document.getElementById("checkboxes").appendChild(checkboxlabel);
        }

        // Add event listener to all checkboxes
        document.querySelector('#checkboxes').onclick = function(ev) {
        if(ev.target.value) {
            console.log(ev.target.checked, ev.target.value);

            // Store checkbox states in Local Storage
            localStorage.setItem(ev.target.value, ev.target.checked);

            // fetchFreeBusy();
            }
        }

        } else {
          console.log('something else hi tucker idiot')
          // appendPre('No upcoming events found.');
        }

    },
    function(err) { console.error("Execute error", err); });
}
