import DragDropFile from './DragDropFile';
import { useState } from 'react'


const Upload = () => {
    const [processedImages, setProcessedImages] = useState(null);

    if (!processedImages) {


        return (
        <div>  

            <h2>Automatically export cropped photos from your scan</h2>
            <p>Free and no download required</p>

            <DragDropFile setProcessedImages={setProcessedImages}/>
        </div>);
    } else {
        return (
            <div>
                <section className="scanningmethods">
                    <p>Detected photos: </p>

                    <div className="form-file-upload"> 
                    <div className = "label-file-upload">
                    <div className="row">
                        {processedImages.map(item => {
                                return <div className="method-col"><h3 className='outputTitle'>Image </h3><img src={item} alt="" /><div className="layer"> </div></div>
                            })}
                    </div>
                    </div>
                    </div>
                </section>
            </div>
        )
    }
};

export default Upload;
